            if (currentEvent?.target) {
              updateButtonText(currentEvent.target, false);
            }
          } finally {
            document.body.removeChild(textarea);
          }
        });
      } catch (error) {
        console.error('Error in copyToClipboard function:', error);
      }
    }
    const sbomData = computed(() => {
